package de.metamorphant.examples.grpcticker;

import java.io.IOException;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import picocli.CommandLine;
import picocli.CommandLine.Command;

@Command(name = "ticker", subcommands = {
    GrpcStreamingTickerAsyncServer.class,
    GrpcStreamingTickerClient.class,
    GrpcStreamingTickerServer.class
})
public class GrpcStreamingTickerApp {
  private static final Logger logger = LoggerFactory.getLogger(GrpcStreamingTickerApp.class.getName());

  public static void main(String[] args) throws IOException, InterruptedException {
    GrpcStreamingTickerApp app = new GrpcStreamingTickerApp();
    new CommandLine(app).execute(args);
  }

}
